+/**
+ * Studies Database Management for Photonic
+ * Handles the Studies table with download workflow management
+ */
+
+const STUDIES_DB_NAME = 'PhotonicStudies';
+const STUDIES_STORE_NAME = 'studies';
+const STUDIES_DB_VERSION = 1;
+
+// Study status constants
+const STUDY_STATUS = {
+ ERROR: 'error',
+ DOWNLOAD: 'download',
+ DOWNLOADED: 'downloaded',
+ DELETED: 'deleted'
+};
+
+/**
+ * Opens the Studies database
+ * @returns {Promise<IDBDatabase>} - The database connection
+ */
+function openStudiesDatabase() {
+ return new Promise((resolve, reject) => {
+ const request = indexedDB.open(STUDIES_DB_NAME, STUDIES_DB_VERSION);
+ 
+ request.onerror = (event) => {
+ console.error('[Photonic] Studies database error:', event.target.error);
+ reject(event.target.error);
+ };
+ 
+ request.onupgradeneeded = (event) => {
+ const db = event.target.result;
+ if (!db.objectStoreNames.contains(STUDIES_STORE_NAME)) {
+ const store = db.createObjectStore(STUDIES_STORE_NAME, { keyPath: 'study_id' });
+ 
+ // Create indexes for efficient querying
+ store.createIndex('status', 'status', { unique: false });
+ store.createIndex('patient_id', 'patient_id', { unique: false });
+ store.createIndex('patient_name', 'patient_name', { unique: false });
+ store.createIndex('diag_centre_name', 'diag_centre_name', { unique: false });
+ store.createIndex('download_time', 'download_time', { unique: false });
+ store.createIndex('delete_time', 'delete_time', { unique: false });
+ store.createIndex('created_at', 'created_at', { unique: false });
+ 
+ console.log('[Photonic] Studies database schema created');
+ }
+ };
+ 
+ request.onsuccess = (event) => {
+ resolve(event.target.result);
+ };
+ });
+}
