+/**
+ * Updates a study's status
+ * @param {string} studyId - The study ID
+ * @param {string} status - The new status
+ * @param {Object} additionalData - Additional data to update
+ * @returns {Promise<void>}
+ */
+async function studiesDbUpdateStatus(studyId, status, additionalData = {}) {
+ try {
+ const study = await studiesDbGet(studyId);
+ if (!study) {
+ throw new Error(`Study not found: ${studyId}`);
+ }
+ 
+ const updatedStudy = {
+ ...study,
+ status,
+ updated_at: new Date().toISOString(),
+ ...additionalData
+ };
+ 
+ // Set timestamp fields based on status
+ if (status === STUDY_STATUS.DOWNLOADED && !updatedStudy.download_time) {
+ updatedStudy.download_time = new Date().toISOString();
+ }
+ if (status === STUDY_STATUS.DELETED && !updatedStudy.delete_time) {
+ updatedStudy.delete_time = new Date().toISOString();
+ }
+ 
+ await studiesDbPut(updatedStudy);
+ } catch (error) {
+ console.error('[Photonic] Studies database updateStatus error:', error);
+ throw error;
+ }
