+/**
+ * Create action buttons for a study
+ */
+function createStudyActions(study) {
+ const actions = [];
+ 
+ if (study.status === STUDY_STATUS.ERROR) {
+ actions.push(`<button class="btn" style="padding: 4px 8px; font-size: 12px;" 
+ onclick="retryStudy('${study.study_id}')">Retry</button>`);
+ }
+ 
+ if (study.status === STUDY_STATUS.DOWNLOADED && study.file_path) {
+ actions.push(`<button class="btn" style="padding: 4px 8px; font-size: 12px;" 
+ onclick="openStudyFile('${study.study_id}')">Open</button>`);
+ }
+ 
+ actions.push(`<button class="btn danger" style="padding: 4px 8px; font-size: 12px;" 
+ onclick="deleteStudy('${study.study_id}')">Delete</button>`);
+ 
+ return actions.join(' ');
