+/**
+ * Simplified study list fetcher for background script
+ */
+async function fetchStudyListFromAPI(credentials) {
+ try {
+ // Authenticate with the API
+ const authResult = await authenticateWithQuickRad(credentials);
+ if (!authResult.success) {
+ throw new Error(`Authentication failed: ${authResult.error}`);
+ }
+ 
+ // Fetch work list
+ const workListResponse = await fetchQuickRadWorkList(authResult.token);
+ 
+ // Parse and store studies
+ const studies = parseQuickRadStudies(workListResponse);
+ const storeResult = await storeStudiesInDB(studies);
+ 
+ return {
+ success: true,
+ message: `Fetched and stored ${studies.length} studies`,
+ total: studies.length,
+ stored: storeResult.stored,
+ updated: storeResult.updated,
+ errors: storeResult.errors
+ };
+ 
+ } catch (error) {
+ console.error('[Photonic] Error fetching study list:', error);
+ return {
+ success: false,
+ error: error.message,
+ total: 0,
+ stored: 0,
+ updated: 0,
+ errors: 1
+ };
+ }
+}
+
+/**
+ * Authenticate with QuickRad API
+ */
+async function authenticateWithQuickRad(credentials) {
+ try {
+ const loginUrl = 'https://toprad.aikenist.com/api/quickrad/telerad/login-validation';
+ 
+ const formData = new FormData();
+ formData.append('email', credentials.username);
+ formData.append('password', credentials.password);
+ 
+ const response = await fetch(loginUrl, {
+ method: 'POST',
+ body: formData,
+ headers: {
+ 'Accept': 'application/json'
+ }
+ });
+ 
+ if (!response.ok) {
+ if (response.status === 429) {
+ throw new Error('Too many login attempts. Account may be temporarily locked.');
+ }
+ throw new Error(`HTTP ${response.status}: ${response.statusText}`);
+ }
+ 
+ const data = await response.json();
+ 
+ if (!data.status || !data.token) {
+ throw new Error(data.message || 'Authentication failed - no valid token received');
+ }
+ 
+ return {
+ success: true,
+ token: data.token,
+ message: data.message
+ };
+ 
+ } catch (error) {
+ return {
+ success: false,
+ error: error.message
+ };
+ }
+}
+
+/**
+ * Fetch work list from QuickRad API
+ */
+async function fetchQuickRadWorkList(token) {
+ const workListUrl = 'https://toprad.aikenist.com/api/quickrad/telerad/fetch-admin-list';
+ 
+ const formData = new FormData();
+ formData.append('page_size', '100');
+ formData.append('page_num', '1');
+ 
+ const response = await fetch(workListUrl, {
+ method: 'POST',
+ body: formData,
+ headers: {
+ 'Authorization': `JWT ${token}`,
+ 'Accept': 'application/json'
+ }
+ });
+ 
+ if (!response.ok) {
+ throw new Error(`HTTP ${response.status}: ${response.statusText}`);
+ }
+ 
+ return await response.json();
+}
+
+/**
+ * Parse QuickRad studies response
+ */
+function parseQuickRadStudies(response) {
+ let studies = [];
+ 
+ if (Array.isArray(response)) {
+ studies = response;
+ } else if (response.study_list && Array.isArray(response.study_list)) {
+ studies = response.study_list;
+ } else if (response.data && Array.isArray(response.data)) {
+ studies = response.data;
+ }
+ 
+ return studies.map(study => ({
+ study_id: study.study_instance_uid,
+ patient_name: cleanPatientNameForFile(study.patient_name || ''),
+ patient_id: study.patient_id || study.mrn || '',
+ diag_centre_name: study.diag_centre_name || study.institution || '',
+ study_instance_uid: study.study_instance_uid,
+ status: 'download', // Set to download status
+ download_time: null,
+ delete_time: null,
+ error: 'None',
+ created_at: new Date().toISOString(),
+ // Additional fields
+ study_date: study.study_date || '',
+ study_time: study.study_time || '',
+ modality: study.modality || '',
+ study_description: study.study_description || ''
+ })).filter(study => study.study_id); // Filter out invalid studies
+}
+
+/**
+ * Clean patient name for filename use
+ */
+function cleanPatientNameForFile(name) {
+ if (!name || typeof name !== 'string') {
+ return 'Unknown_Patient';
+ }
+ 
+ let cleaned = name
+ .replace(/[^A-Za-z0-9\s\-_]/g, '')
+ .replace(/\s+/g, '_')
+ .replace(/_{2,}/g, '_')
+ .replace(/^_+|_+$/g, '')
+ .trim();
+ 
+ if (!cleaned) {
+ cleaned = 'Unknown_Patient';
+ }
+ 
+ if (cleaned.length > 50) {
+ cleaned = cleaned.substring(0, 50);
+ }
+ 
+ return cleaned;
+}
+
+/**
+ * Store studies in the database (simplified version for background script)
+ */
+async function storeStudiesInDB(studies) {
+ // For now, we'll use chrome.storage.local to store studies
+ // In a full implementation, we'd use the IndexedDB functions
+ 
+ try {
+ const { photonic_studies } = await chrome.storage.local.get(['photonic_studies']);
+ const existingStudies = photonic_studies || {};
+ 
+ let stored = 0;
+ let updated = 0;
+ 
+ for (const study of studies) {
+ if (existingStudies[study.study_id]) {
+ // Update existing study (preserve status and timestamps)
+ existingStudies[study.study_id] = {
+ ...study,
+ status: existingStudies[study.study_id].status,
+ download_time: existingStudies[study.study_id].download_time,
+ delete_time: existingStudies[study.study_id].delete_time,
+ updated_at: new Date().toISOString()
+ };
+ updated++;
+ } else {
+ // Store new study
+ existingStudies[study.study_id] = study;
+ stored++;
+ }
+ }
+ 
+ await chrome.storage.local.set({ photonic_studies: existingStudies });
+ 
+ return { stored, updated, errors: 0 };
+ 
+ } catch (error) {
+ console.error('[Photonic] Error storing studies:', error);
+ return { stored: 0, updated: 0, errors: studies.length };
+ }
+}
+
+/**
+ * Trigger study downloads from background script
+ */
+async function triggerStudyDownloadsFromBackground(credentials) {
+ try {
+ // Get studies marked for download
+ const { photonic_studies } = await chrome.storage.local.get(['photonic_studies']);
+ const allStudies = photonic_studies || {};
+ 
+ const studiesToDownload = Object.values(allStudies).filter(study => 
+ study.status === 'download'
+ );
+ 
+ if (studiesToDownload.length === 0) {
+ return {
+ success: true,
+ message: 'No studies marked for download',
+ total: 0,
+ downloaded: 0,
+ errors: 0
+ };
+ }
+ 
+ // Authenticate
+ const authResult = await authenticateWithQuickRad(credentials);
+ if (!authResult.success) {
+ throw new Error(`Authentication failed: ${authResult.error}`);
+ }
+ 
+ // Download studies (simplified - just mark as downloaded for now)
+ let downloaded = 0;
+ let errors = 0;
+ 
+ for (const study of studiesToDownload.slice(0, 5)) { // Limit to 5 for demo
+ try {
+ // In a real implementation, we'd download the actual files
+ // For now, just mark as downloaded
+ allStudies[study.study_id] = {
+ ...study,
+ status: 'downloaded',
+ download_time: new Date().toISOString()
+ };
+ downloaded++;
+ } catch (error) {
+ console.error(`[Photonic] Error downloading study ${study.study_id}:`, error);
+ allStudies[study.study_id] = {
+ ...study,
+ status: 'error',
+ error: error.message
+ };
+ errors++;
+ }
+ }
+ 
+ await chrome.storage.local.set({ photonic_studies: allStudies });
+ 
+ return {
+ success: true,
+ message: `Download completed: ${downloaded} successful, ${errors} errors`,
+ total: studiesToDownload.length,
+ downloaded,
+ errors
+ };
+ 
+ } catch (error) {
+ console.error('[Photonic] Error triggering downloads:', error);
+ return {
+ success: false,
+ error: error.message,
+ total: 0,
+ downloaded: 0,
+ errors: 1
+ };
+ }
+}
