+#!/usr/bin/env python3
+"""
+Simple script to create a basic icon for the Photonic extension
+"""
+from PIL import Image, ImageDraw, ImageFont
+import os
+
+def create_icon():
+ # Create a 48x48 image with a blue background
+ size = 48
+ img = Image.new('RGBA', (size, size), (66, 133, 244, 255)) # Google Blue
+ draw = ImageDraw.Draw(img)
+ 
+ # Draw a simple "P" for Photonic
+ try:
+ # Try to use a system font
+ font = ImageFont.truetype("arial.ttf", 32)
+ except:
+ # Fallback to default font
+ font = ImageFont.load_default()
+ 
+ # Draw white "P" in the center
+ text = "P"
+ bbox = draw.textbbox((0, 0), text, font=font)
+ text_width = bbox[2] - bbox[0]
+ text_height = bbox[3] - bbox[1]
+ 
+ x = (size - text_width) // 2
+ y = (size - text_height) // 2
+ 
+ draw.text((x, y), text, fill=(255, 255, 255, 255), font=font)
+ 
+ # Save the icon
+ icon_path = os.path.join(os.path.dirname(__file__), 'icons', 'icon48.png')
+ img.save(icon_path, 'PNG')
+ print(f"Icon created: {icon_path}")
+
+if __name__ == "__main__":
+ create_icon()","refToolCallId":"toolu_01M8PkGykWK2yW3qzK1qqkjY"},{"type":"toolCall","toolCallId":"toolu_01H4T4ynXL8Hay9PBf9dLwNz","status":"IN_PROGRESS","isHidden":true,"localResult":"{"status":"SUCCESS","exitCode":0,"output":"\u001b[?9001h\u001b[?1004h\u001b[?25l\u001b[2J\u001b[m\u001b[HIcon created: C:\\Users\\LuisRamos\\PycharmProjects\\Photonic\\icons\\icon48.png\r\n\u001b]0;C:\\Windows\\System32\\WindowsPowerShell\\v1.0\\powershell.exe\u0007\u001b[?25h"}"},{"type":"codePatch","diffs":[{"path":"create_icon.py","patch":"@@ -0,0 +1,1143 @@
