 /**
+ * Open a downloaded study in Radiant Viewer
+ */
+async function openStudyInRadiant(studyId) {
+ try {
+ const study = await studiesDbGet(studyId);
+ if (!study || !study.file_path) {
+ throw new Error('Study file not found');
+ }
+ 
+ const settings = getCurrentSettings();
+ 
+ if (!settings.radiantExecutablePath) {
+ showNotification('Radiant executable path not configured. Please check settings.', 'error');
+ return;
+ }
+ 
+ console.log(`[Photonic] Opening study ${studyId} in Radiant Viewer`);
+ console.log(`[Photonic] Study file path: ${study.file_path}`);
+ console.log(`[Photonic] Radiant path: ${settings.radiantExecutablePath}`);
+ 
+ // In a browser extension environment, we can't directly execute external programs
+ // We need to use the native messaging API or show instructions to the user
+ if (typeof chrome !== 'undefined' && chrome.runtime) {
+ // Extension environment - show instructions or use native messaging
+ const message = `To open this study in Radiant Viewer:\n\n` +
+ `1. Open Radiant Viewer manually\n` +
+ `2. Use File > Open to navigate to:\n` +
+ ` ${study.file_path}\n\n` +
+ `Or configure native messaging for automatic opening.`;
+ 
+ if (confirm(message + '\n\nCopy file path to clipboard?')) {
+ // Copy to clipboard if possible
+ try {
+ await navigator.clipboard.writeText(study.file_path);
+ showNotification('File path copied to clipboard', 'success');
+ } catch (e) {
+ showNotification('Could not copy to clipboard. File path: ' + study.file_path, 'info');
+ }
+ }
+ } else {
+ // Standalone environment - could potentially use Node.js child_process if available
+ showNotification(`Opening study in Radiant Viewer...\nFile: ${study.file_path}`, 'info');
+ 
+ // For now, just show the command that would be executed
+ const command = `"${settings.radiantExecutablePath}" ${settings.radiantCommandArgs} "${study.file_path}"`;
+ console.log(`[Photonic] Would execute: ${command}`);
+ 
+ // In a real implementation, you might use:
+ // const { exec } = require('child_process');
+ // exec(command, (error, stdout, stderr) => { ... });
+ }
+ 
+ } catch (error) {
+ console.error('[Photonic] Error opening study in Radiant:', error);
+ showNotification('Error opening study in Radiant: ' + error.message, 'error');
+ }
