+function getFolderPath(filePath) {
+ const userAgent = navigator.userAgent;
+ const platform = navigator.platform;
+ 
+ if (platform.indexOf('Win') !== -1 || userAgent.indexOf('Windows') !== -1) {
+ // Windows: use backslash separator
