+ return lastBackslash !== -1 ? filePath.substring(0, lastBackslash) : filePath;
+ } else {
+ // macOS/Linux: use forward slash separator
+ const lastSlash = filePath.lastIndexOf('/');
+ return lastSlash !== -1 ? filePath.substring(0, lastSlash) : filePath;
+ }
