WL_URL = f"{BASE}/api/quickrad/telerad/fetch-admin-list"
MISC_URL = f"{BASE}/api/quickrad/general/get-misc-study-data"
ARCHIVE_TPL = f"{BASE}/dicom-web/studies/{{uuid}}/archive"

