PASSWORD = "Toprad@312"
CACHE = Path("~/.photonic_token.json").expanduser()

logging.basicConfig(level="INFO", format="%(levelname)s %(message)s")
log = logging.getLogger("photonic")

# Token handling 
@dataclass
class TokenCache:
 access_token: str
 exp_epoch: int
 def good(self): return self.exp_epoch - 30 > time.time()
 def save(self): CACHE.write_text(json.dumps(self.__dict__))
 @classmethod
 def load(cls):
 try: return cls(**json.loads(CACHE.read_text()))
 except Exception: return None

def _jwt_exp(tok: str) -> int:
 pl = tok.split(".")[1] + "=="
 return json.loads(base64.urlsafe_b64decode(pl))["exp"]

# Session wrapper 
class PhotonicSession:
 def __init__(self):
 self.s: Session = requests.Session()
 self.cache = TokenCache.load()
 if not (self.cache and self.cache.good()):
 self._login()

 # internal helpers ------------------------------------------------
 def _auth(self, kw):
 kw.setdefault("headers", {})["Authorization"] = f"JWT {self.cache.access_token}"
 return kw
 def get(self, url: str, **kw): self._ensure(); return self.s.get(url, **self._auth(kw))
 def post(self, url: str, **kw): self._ensure(); return self.s.post(url, **self._auth(kw))
 def _ensure(self):
 if not self.cache.good(): self._login()

 def _login(self):
 log.info(" Logging in")
 files = {"email": (None, USERNAME), "password": (None, PASSWORD)}
 r = self.s.post(LOGIN_URL, files=files, headers={"Accept": "application/json"}, timeout=10)
 r.raise_for_status()
 tok = r.json()["token"]
 self.cache = TokenCache(tok, _jwt_exp(tok)); self.cache.save()
 log.info(" JWT cached expires %s" % time.strftime("%Y-%m-%d %H:%M", time.localtime(self.cache.exp_epoch)))

# Helpers 

def extract_studies(obj):
 return obj if isinstance(obj, list) else obj.get("study_list", [])

def slugify(name: str) -> str:
 """Sanitise patient name for use as filename."""
 name = re.sub(r"[^A-Za-z0-9 _-]+", "", name).strip()
 name = re.sub(r"[\s]+", "_", name)
 return name or "patient"

# QuickRad calls 

def fetch_internal_uuid(api: PhotonicSession, study_instance_uid: str) -> str:
