+/**
+ * Encrypts a password string using AES-GCM
+ * @param {string} password - The password to encrypt
+ * @returns {Promise<Object>} - Object containing encrypted data and key info
+ */
+async function encryptPassword(password) {
+ try {
+ // Generate a random encryption key
+ const key = await window.crypto.subtle.generateKey(
+ { name: 'AES-GCM', length: 256 },
+ true,
+ ['encrypt', 'decrypt']
+ );
+ 
+ // Generate a random initialization vector
+ const iv = window.crypto.getRandomValues(new Uint8Array(12));
+ 
+ // Export the key for storage
+ const exportedKey = await window.crypto.subtle.exportKey('raw', key);
+ 
+ // Convert password to ArrayBuffer for encryption
+ const encoder = new TextEncoder();
+ const passwordBuffer = encoder.encode(password);
+ 
+ // Encrypt the password
+ const cipherText = await window.crypto.subtle.encrypt(
+ { name: 'AES-GCM', iv },
+ key,
+ passwordBuffer
+ );
+ 
+ return {
+ cipherText: Array.from(new Uint8Array(cipherText)),
+ key: Array.from(new Uint8Array(exportedKey)),
+ iv: Array.from(iv)
+ };
+ } catch (error) {
+ console.error('[Photonic] Password encryption error:', error);
+ throw new Error('Failed to encrypt password: ' + error.message);
+ }
+}
+
+/**
+ * Decrypts a previously encrypted password
+ * @param {Object} encryptedData - Object containing cipherText, key, and iv
+ * @returns {Promise<string>} - The decrypted password
+ */
+async function decryptPassword(encryptedData) {
+ try {
+ // Convert arrays back to Uint8Arrays
+ const keyArray = new Uint8Array(encryptedData.key);
+ const ivArray = new Uint8Array(encryptedData.iv);
+ const cipherArray = new Uint8Array(encryptedData.cipherText);
+ 
+ // Import the encryption key
+ const key = await window.crypto.subtle.importKey(
+ 'raw',
+ keyArray,
+ { name: 'AES-GCM', length: 256 },
+ false,
+ ['decrypt']
+ );
+ 
+ // Decrypt the password
+ const decryptedBuffer = await window.crypto.subtle.decrypt(
+ { name: 'AES-GCM', iv: ivArray },
+ key,
+ cipherArray
+ );
+ 
+ // Convert back to string
+ const decoder = new TextDecoder();
+ return decoder.decode(decryptedBuffer);
+ } catch (error) {
+ console.error('[Photonic] Password decryption error:', error);
+ throw new Error('Failed to decrypt password: ' + error.message);
+ }
+}
+
 // Export the functions
-export { encryptBlob, decryptToBlob };
+export { encryptBlob, decryptToBlob, encryptPassword, decryptPassword };","refToolCallId":"toolu_01CWX118AAPWrHnf1mRtsjZh"},{"type":"text","text":"## 2. Now let's update the options.js file to fix password storage and improve test connection:"},{"type":"toolCall","toolCallId":"toolu_01JDNeA7Qc7iEzPPSUYwk28z","statusText":"Change generated","status":"SUCCESS","context":{"files":[{"path":"options.js","fsPath":"C:\Users\LuisRamos\PycharmProjects\Photonic\options.js"}]},"isHidden":false},{"type":"codeHunk","lineBasedDiff":"--- options.js
