+ <script>
+ async function testConnection() {
+ const username = document.getElementById('username').value.trim();
+ const password = document.getElementById('password').value;
+ const resultDiv = document.getElementById('result');
+ 
+ if (!username || !password) {
+ showResult('Please enter both username and password', 'error');
+ return;
+ }
+ 
+ showResult('Testing connection...', 'info');
+ 
+ try {
+ // Step 1: Authenticate
+ console.log('Step 1: Authenticating...');
+ const loginUrl = 'https://toprad.aikenist.com/api/quickrad/telerad/login-validation';
+ 
+ const formData = new FormData();
+ formData.append('email', username);
+ formData.append('password', password);
+ 
+ const loginResponse = await fetch(loginUrl, {
+ method: 'POST',
+ body: formData,
+ headers: {
+ 'Accept': 'application/json'
+ }
+ });
+ 
+ if (!loginResponse.ok) {
+ if (loginResponse.status === 429) {
+ showResult('Too many login attempts. Account may be temporarily locked.', 'error');
+ return;
+ } else if (loginResponse.status === 401 || loginResponse.status === 403) {
+ showResult('Authentication failed. Please check your username and password.', 'error');
+ return;
+ } else {
+ showResult(`Login failed: HTTP ${loginResponse.status}: ${loginResponse.statusText}`, 'error');
+ return;
+ }
+ }
+ 
+ const loginData = await loginResponse.json();
+ console.log('Login response:', loginData);
+ 
+ if (!loginData.status || !loginData.token) {
+ showResult(loginData.message || 'Authentication failed - no valid token received', 'error');
+ return;
+ }
+ 
+ const token = loginData.token;
+ console.log(' Authentication successful');
+ 
+ // Step 2: Fetch work-list
+ console.log('Step 2: Fetching work-list...');
+ const workListUrl = 'https://toprad.aikenist.com/api/quickrad/telerad/fetch-admin-list';
+ 
+ const workListFormData = new FormData();
+ workListFormData.append('page_size', '10');
+ workListFormData.append('page_num', '1');
+ 
+ const workListResponse = await fetch(workListUrl, {
+ method: 'POST',
+ body: workListFormData,
+ headers: {
+ 'Authorization': `JWT ${token}`,
+ 'Accept': 'application/json'
+ }
+ });
+ 
+ if (!workListResponse.ok) {
+ showResult(`Work-list fetch failed: HTTP ${workListResponse.status}: ${workListResponse.statusText}`, 'error');
+ return;
+ }
+ 
+ const workListData = await workListResponse.json();
+ console.log('Work-list response:', workListData);
+ 
+ // Extract studies
+ let studies = [];
+ if (Array.isArray(workListData)) {
+ studies = workListData;
+ } else if (workListData.study_list && Array.isArray(workListData.study_list)) {
+ studies = workListData.study_list;
+ }
+ 
+ showResult(` Connection successful! Found ${studies.length} studies in worklist.`, 'success');
+ 
+ } catch (error) {
+ console.error('Test error:', error);
+ if (error.message.includes('Failed to fetch')) {
+ showResult('Network error. Please check your internet connection.', 'error');
+ } else {
+ showResult(`Connection test failed: ${error.message}`, 'error');
+ }
+ }
+ }
+ 
+ function showResult(message, type) {
+ const resultDiv = document.getElementById('result');
+ resultDiv.textContent = message;
+ resultDiv.className = type;
+ }
